import { Component, OnInit } from '@angular/core';
import { ProductService } from './product.service';
import { Product } from './product.model';
import { CurrencyService } from './currency/currency.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent implements OnInit {
  products: Product[] = [];
  selectedCurrency: string = 'USD';

  constructor(private productService: ProductService, private currencyService: CurrencyService) {}

  ngOnInit() {
    this.loadProducts();
  }

  loadProducts() {
    this.productService.getProducts().subscribe(products => (this.products = products));
  }

  onCurrencyChange(currency: string) {
    this.selectedCurrency = currency;
    this.currencyService.setCurrency(currency);
  }

  convertPrice(price: number, targetCurrency: string): number {
    // Implement logic for currency conversion here
    // This is a placeholder and should be replaced with actual conversion logic
    return price;
  }
}
