import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Product } from '../product.model';
import { ExchangeRate } from '../exchange.model';
import { Country } from '../country.model';
@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private apiUrl = 'https://localhost:44431/products'; 
  
  private conversionRates = {
    GBP: 1,
    USD: 1.24,   
    EUR: 1.14,  
    AUD: 1.92
  };
  constructor(private http: HttpClient) {}

  getProducts(): Observable<Product[]> {
    return this.http.get<Product[]>(this.apiUrl);
  }

  convertPrice(productId: number, newCurrency: string, exchangeRates: ExchangeRate, countries: Country): Observable<Product> {

    const convertUrl = `${this.apiUrl}/convertPrice/${productId}?newCurrency=${newCurrency}/exchangeRates=${exchangeRates}/countries=${countries}`;
    return this.http.put<Product>(convertUrl, {});
  }

  updateProductPrice(id: number, newPrice: number, newCurrency: string): Observable<Product> {
    const updateUrl = `${this.apiUrl}/${id}`;
    return this.http.put<Product>(updateUrl, { price: newPrice, currency: newCurrency });
  }
}
